mapper.select:
SELECT num, title, sex, people_cnt, visiter_name, phone, visit_day, reser_time, visit_reason, id, visit_identify FROM ( SELECT /*+INDEX_DESC(reservation pk_reservation) */ rownum rn, num, title, sex, people_cnt, visiter_name, phone, visit_day, reser_time, visit_reason, id, visit_identify FROM reservation WHERE
(visit_day BETWEEN TO_DATE(#{startday}) AND TO_DATE(#{endday})+0.99999) AND
rownum <= #{pageNum} * #{amount} AND id = #{id} ) WHERE rn > (#{pageNum} -1) * #{amount}

SELECT adoptnum, name, phone, birthday_date, address, board_num, servey1, servey2, servey3, servey4, servey5, servey6, servey7, servey8, reg_date, id FROM ( SELECT /*+INDEX_DESC(reservation pk_adopt_reservation) */ rownum rn, adoptnum, name, phone, birthday_date, address, board_num, servey1, servey2, servey3, servey4, servey5, servey6, servey7, servey8, reg_date, id FROM adopt_reservation WHERE
(reg_date BETWEEN TO_DATE(#{startday}) AND TO_DATE (#{endday})+0.99999) AND
rownum <= #{pageNum} * #{amount} AND id = #{id} ) WHERE rn > (#{pageNum} -1) * #{amount}

SELECT COUNT(*) FROM reservation WHERE
(visit_day BETWEEN TO_DATE(#{startday}) AND TO_DATE(#{endday})+0.99999) AND
num > 0 AND id = #{id}

SELECT COUNT(*) FROM adopt_reservation WHERE
(reg_date BETWEEN TO_DATE(#{startday}) AND TO_DATE(#{endday})+0.99999) AND
adoptnum > 0 AND id = #{id}

SELECT perchase_num, perchased_product, all_price, ordered_date, delivery_status, product_num, id FROM ( SELECT /*+INDEX_DESC(product_perchase PK_PRODUCT_PERCHASE) */ rownum rn, perchase_num, perchased_product, all_price, ordered_date, delivery_status, product_num, id FROM product_perchase WHERE
(ordered_date BETWEEN TO_DATE(#{startday}) AND TO_DATE (#{endday})+0.99999) AND
rownum <= #{pageNum} * #{amount} ) WHERE rn > (#{pageNum} -1) * #{amount} AND id = #{id}

SELECT COUNT(*)
FROM product_perchase
WHERE perchase_num > 0 AND id = #{id}
